import 'dotenv/config';
import { analyzeFace } from './controllers/ml.controller.js';
import fs from 'fs';

// Valid 1x1 JPEG Base64
const validJpegBase64 = "";
const imageBuffer = Buffer.from(validJpegBase64, 'base64');

// Mock Req/Res
const req = {
    userId: "test-user-id",
    body: { scanType: "eyes" },
    file: {
        buffer: imageBuffer,
        mimetype: "image/jpeg"
    }
};

const res = {
    status: (code) => {
        console.log(`Response Status: ${code}`);
        return {
            json: (data) => console.log("Response Data:", JSON.stringify(data, null, 2))
        };
    }
};

console.log("Testing analyzeFace with Valid 1x1 JPEG...");
try {
    await analyzeFace(req, res);
} catch (err) {
    console.error("Caught Unexpected Error:", err);
    fs.writeFileSync('error_details.txt', err.toString() + "\n" + (err.stack || ""));
}
